import { Add } from "@mui/icons-material";
import { TabContext, TabList } from "@mui/lab";
import {
  Box,
  Button,
  Card,
  Grid,
  styled,
  Tab,
  useMediaQuery,
} from "@mui/material";
import FlexBox from "components/flexbox/FlexBox";
import SearchInput from "components/input-fields/SearchInput";
import { H2, H6, Small } from "components/Typography";
import CreateProject from "page-sections/uko-projects/CreateProject";
import ProjectCard3 from "page-sections/uko-projects/ProjectCard3";
import React, { useEffect, useState } from "react";
import { searchByName } from "utils/utils"; // styled component

const TopAreaWrapper = styled(Card)(({ theme }) => ({
  display: "flex",
  justifyContent: "space-between",
  alignItems: "flex-end",
  flexWrap: "wrap",
  [theme.breakpoints.down(700)]: {
    "& h2": {
      paddingTop: "1rem",
    },
    "& img": {
      display: "none",
    },
  },
}));
const StyledTabList = styled(TabList)(({ theme }) => ({
  "& .MuiTabs-flexContainer .MuiButtonBase-root": {
    marginLeft: 0,
    marginRight: 0,
    paddingLeft: "1rem",
    paddingRight: "1rem",
    borderBottom: "2px solid",
    borderColor: theme.palette.divider,
  },
  "& .MuiTabs-flexContainer .Mui-selected": {
    color: theme.palette.text.primary,
  },
  [theme.breakpoints.down(1064)]: {
    maxWidth: 600,
  },
  [theme.breakpoints.between(700, 838)]: {
    maxWidth: 475,
  },
  [theme.breakpoints.down("sm")]: {
    maxWidth: 320,
  },
}));

const UkoProjectV3 = () => {
  const [value, setValue] = useState("all");
  const [openModal, setOpenModal] = useState(false);
  const downSM = useMediaQuery((theme) => theme.breakpoints.down("sm"));

  const handleChange = (_, newValue) => {
    setValue(newValue);
  }; // search input

  const [searchValue, setSearchValue] = useState("");
  const [filteredItem, setFilteredItem] = useState(projectList);
  useEffect(() => {
    const result = searchByName(projectList, searchValue);
    setFilteredItem(result);
  }, [searchValue]);
  const filterList = filteredItem.filter(
    (item) => value === "all" || item.status === value
  );
  return (
    <Box pt={2} pb={4}>
      <TopAreaWrapper>
        <Box>
          <H2 paddingLeft={3} paddingBottom={1}>
            Uko Projects
          </H2>
          <TabContext value={value}>
            <StyledTabList variant="scrollable" onChange={handleChange}>
              {tabItems.map((item) => (
                <Tab
                  disableRipple
                  key={item.title}
                  value={item.title.split(" ").join("-").toLowerCase()}
                  label={
                    <FlexBox gap={1} alignItems="center">
                      <H6 fontSize={12}>{item.title}</H6>
                      <Small
                        sx={{
                          backgroundColor: "divider",
                          padding: "0px 10px",
                          borderRadius: "10px",
                        }}
                      >
                        {item.amount}
                      </Small>
                    </FlexBox>
                  }
                />
              ))}
            </StyledTabList>
          </TabContext>
        </Box>
        <Box padding="0.5rem 1rem">
          <img
            alt="UKO Projects"
            src="/static/illustration/uko-project-v1.svg"
          />
        </Box>
      </TopAreaWrapper>

      <Grid container spacing={3} paddingTop={3}>
        <Grid item xs={12}>
          <FlexBox justifyContent="space-between" flexWrap="wrap">
            <SearchInput
              disable_border
              placeholder="Find Projects"
              sx={{
                maxWidth: downSM ? "100%" : 270,
                marginBottom: downSM ? 1 : 0,
              }}
              onChange={(e) => setSearchValue(e.target.value)}
            />
            <Button
              fullWidth={downSM}
              variant="contained"
              startIcon={<Add />}
              onClick={() => setOpenModal(true)}
              sx={{
                fontSize: 12,
              }}
            >
              Create a project
            </Button>

            <CreateProject open={openModal} setOpen={setOpenModal} />
          </FlexBox>
        </Grid>

        {filterList.map((item, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <ProjectCard3 project={item} />
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

const tabItems = [
  {
    title: "All",
    amount: 45,
  },
  {
    title: "In Progress",
    amount: 12,
  },
  {
    title: "Upcoming",
    amount: 12,
  },
  {
    title: "Blocked",
    amount: 12,
  },
  {
    title: "Completed",
    amount: 12,
  },
];
const projectList = [
  {
    name: "Project Nightfall",
    description:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor ut labore et dolore magna aliqua.",
