import { Add, MoreHoriz } from "@mui/icons-material";
import {
  AvatarGroup,
  Box,
  Button,
  Card,
  Divider,
  Grid,
  IconButton,
  styled,
} from "@mui/material";
import FormControlLabel from "@mui/material/FormControlLabel";
import LinearProgress from "@mui/material/LinearProgress";
import AppAvatar from "components/avatars/AppAvatar";
import FlexBetween from "components/flexbox/FlexBetween";
import FlexBox from "components/flexbox/FlexBox";
import MoreOptions from "components/MoreOptions";
import RoundCheckBox from "components/RoundCheckBox";
import { H3, H5, H6, Small, Tiny } from "components/Typography";
import { useState } from "react";
const StyledAvatar = styled(AppAvatar)(() => ({
  width: 36,
  height: 36,
  borderColor: "transparent",
  backgroundColor: "transparent",
}));
const RightContentWrapper = styled(Box)(() => ({
  height: "100%",
  display: "flex",
  flexDirection: "column",
  justifyContent: "space-between",
}));

const ProjectDetails = () => {
  const [fileEl, setFileEl] = useState(null);
  const [projectEl, setProjectEl] = useState(null); // -----------------------------------------------------------------------

  const handleProjectMoreOpen = (event) => {
    setProjectEl(event.currentTarget);
  };

  const handleProjectMoreClose = () => setProjectEl(null); // -----------------------------------------------------------------------

  const handleFileMoreOpen = (event) => {
    setFileEl(event.currentTarget);
  };

  const handleFileMoreClose = () => setFileEl(null); // -----------------------------------------------------------------------

  return (
    <Box pt={2} pb={4}>
      <Grid container spacing={3}>
        <Grid item xs={12} md={8}>
          <Card>
            <Box padding={3}>
              <FlexBetween>
                <H3 mb={1}>Project Nightfall</H3>
                <IconButton
                  sx={{
                    padding: 0,
                  }}
                  onClick={handleProjectMoreOpen}
                >
                  <MoreHoriz />
                </IconButton>

                <MoreOptions
                  anchorEl={projectEl}
                  handleMoreClose={handleProjectMoreClose}
                />
              </FlexBetween>

              <Small color="text.secondary">
                Lorem ipsum dolor sit amet, consectetur adipiscing Lorem ipsum
                dolor sit amet, consectetur adipiscing elit, sed do eiusmod
                tempor ut labore et dolore magna aliqua. elit, sed do eiusmod
                tempor ut labore et dolore magna aliqua. sed do eiusmod tempor
                ut labore.
              </Small>
            </Box>

            <Divider />

            <Box padding={3}>
              <Grid container spacing={3}>
                <Grid item sm={7} xs={12}>
                  <H5 mb={2}>Tasks</H5>
                  {tasks.map((task) => (
                    <FormControlLabel
                      key={task.title}
                      control={
                        <RoundCheckBox checked={task.status === "Completed"} />
                      }
                      label={
                        <Box>
                          <H6 lineHeight={1} mb={0.3}>
                            {task.title}
                          </H6>
                          <Tiny color="text.secondary" fontWeight={500}>
                            {task.status}
                          </Tiny>
                        </Box>
                      }
                      sx={{
                        margin: 0,
                        width: "100%",
                        cursor: "default",
                        paddingBottom: 1.5,
                        alignItems: "flex-start",
                        "& .MuiCheckbox-root": {
                          padding: 0,
                          paddingRight: 1.2,
                        },
                        "&:last-child": {
                          paddingBottom: 0,
                        },
                      }}
                    />
                  ))}
                </Grid>

                <Grid item sm={5} xs={12}>
                  <H5 mb={2}>Team</H5>
                  <AvatarGroup
                    sx={{
                      alignItems: "center",
                      justifyContent: "flex-end",
                      "& .MuiAvatar-root": {
                        boxSizing: "border-box",
                        border: 0,
                      },
                    }}
                  >
                    <AppAvatar
                      alt="Remy Sharp"
                      src="/static/avatar/001-man.svg"
                    />
                    <AppAvatar
                      alt="Travis Howard"
                      src="/static/avatar/002-girl.svg"
                    />
                    <AppAvatar
                      alt="Cindy Baker"
                      src="/static/avatar/003-boy.svg"
                    />

                    <Button
                      variant="dashed"
                      sx={{
                        ml: 1,
                      }}
                    >
                      <Add
                        fontSize="small"
                        sx={{
                          color: "grey.600",
                        }}
                      />
                    </Button>
                  </AvatarGroup>

                  <Box mt={2}>
                    <FlexBetween py={1}>
                      <H6 fontWeight={600}>Project Progress</H6>
                      <H6>32%</H6>
                    </FlexBetween>

                    <LinearProgress variant="determinate" value={32} />
                  </Box>
                </Grid>
              </Grid>
            </Box>

            <Divider />

            <Box padding={3}>
              <H5 mb={2}>File Attachment</H5>
